package com.java_config_practice.controller;

public class test1 {

	public static int solution(int N) {
		
       int answer =0;
       int temp=N;
       while(temp>0) {
    	   answer+= temp;
    	   temp--;
    	   
       }
		
		return N;
		
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		solution(7);
	}

}
